import React, { PureComponent } from 'react';
import {
  PieChart, Pie, Tooltip, Legend,
} from 'recharts';

import data from '../../data';
import { countWins } from '../../utils/players';

const _ = require('lodash/core');

const flattenRaces = _.flattenDeep(_.map(data.Heats, 'races'));
const winnersCount = countWins(flattenRaces);
const countArr = Object.entries(winnersCount);

const data01 = [{ name: 'New Tasks', value: 40, fill: '#4ce1b6' },
{ name: 'Done Tasks', value: 175, fill: '#70bbfd' },
{ name: 'High Priority Task', value: 70, fill: '#ff4861' }];

const data02 = [{ name: 'Minor Bugs', value: 150, fill: '#4ce1b6' },
{ name: 'Resolved Bugs', value: 75, fill: '#70bbfd' },
{ name: 'Resolved Bugs', value: 25, fill: '#70bbfd' },
{ name: 'Critical Bugs', value: 75, fill: '#ff4861' }];

const style = {
  top: 30,
  left: 240,
  width: 150,
  lineHeight: '24px',
};

const Statistics = () => (

  <div>
    {/* project-summary__statistic */}
    <div>
      {/* project-summary__statistic-title */}
      <p>Top Winners</p>
      <PieChart width={240} height={220}>
        <Tooltip />
        <Pie data={data01} dataKey="value" cx={110} cy={110} innerRadius={60} outerRadius={80} label />
        <Legend layout="vertical" verticalAlign="middle" wrapperStyle={style} />
      </PieChart>
    </div>
    <div>
      {/* project-summary__statistic-title */}
      <p>label 2</p>
      <PieChart width={240} height={250}>
        <Tooltip />
        <Pie data={data02} dataKey="value" cx={110} cy={110} innerRadius={60} outerRadius={80} label />
        <Legend layout="vertical" verticalAlign="middle" wrapperStyle={style} />
      </PieChart>
    </div>
  </div>
);

export default Statistics;
