import React from 'react';
import {
  PieChart, Pie, Tooltip, Legend, RadialBarChart, RadialBar, ResponsiveContainer
} from 'recharts';

import data from '../../data';
import { sortedWinnerData, sortedChampWinnerData } from '../../utils/dataHelpers';

const _ = require('lodash/core');

// overall winners data
const flattenRaces = _.flattenDeep(_.map(data.Heats, 'races'));
const winnersData = sortedWinnerData(flattenRaces);
const topThree = winnersData.slice(0, 3);

// championship winners data
const flattenHeats = _.flattenDeep(_.map(data.Heats));
const champsData = sortedChampWinnerData(flattenHeats);
const topThreeChamps = champsData.slice(0, 3);

const colors = ['#4ce1b6', '#70bbfd', '#ff4861', '#4ce1b6', '#70bbfd', '#ff4861', '#4ce1b6', '#70bbfd', '#ff4861'];

const style = {
  top: 30,
  left: 240,
  width: 150,
  lineHeight: '24px',
};

const testdata = [
  {
    name: '18-24', uv: 31.47, pv: 2400, fill: '#8884d8',
  },
  {
    name: '25-29', uv: 26.69, pv: 4567, fill: '#83a6ed',
  },
  {
    name: '30-34', uv: 15.69, pv: 1398, fill: '#8dd1e1',
  },
  {
    name: '35-39', uv: 8.22, pv: 9800, fill: '#82ca9d',
  },
  {
    name: '40-49', uv: 8.63, pv: 3908, fill: '#a4de6c',
  },
  {
    name: '50+', uv: 2.63, pv: 4800, fill: '#d0ed57',
  },
  {
    name: 'unknown', uv: 6.67, pv: 4800, fill: '#ffc658',
  },
];

const PlayerStatistics = () => (

  <div>
    <div className="tracks_stats_container">
      <div className="tracks_stats">
        <p className="chart_title">Top Overall Wins</p>
        <PieChart width={240} height={220}>
          <Tooltip />
          <Pie
            data={topThree.map((val, i) => (
              { name: val[0], value: val[1], fill: colors[i] }
            ))}
            dataKey="value"
            cx={110}
            cy={110}
            innerRadius={60}
            outerRadius={80}
            label
          />
          <Legend layout="vertical" verticalAlign="middle" wrapperStyle={style} />
        </PieChart>
      </div>
      <div>
        <p>Top Championship Wins</p>
        <PieChart width={240} height={250}>
          <Tooltip />
          <Pie
            data={topThreeChamps.map((val, i) => (
              { name: val[0], value: val[1].value, fill: colors[i] }
            ))}
            dataKey="value"
            cx={110}
            cy={110}
            innerRadius={60}
            outerRadius={80}
            label
          />
          <Legend layout="vertical" verticalAlign="middle" wrapperStyle={style} />
        </PieChart>
      </div>
    </div>

    <div className="tracks_stats_container">
      <div className="tracks_stats">
        <p>Top Overall Wins</p>
        <ResponsiveContainer height={320}>
          <RadialBarChart cy={130} innerRadius={10} outerRadius={140} barSize={10} data={testdata}>
            <Tooltip />
            <RadialBar minAngle={15} background clockWise dataKey="uv" />
            <Legend iconSize={10} wrapperStyle={style} />
          </RadialBarChart>
        </ResponsiveContainer>
      </div>
      <div>
        <p>Top Championship Wins</p>
        <PieChart width={240} height={250}>
          <Tooltip />
          <Pie
            data={topThreeChamps.map((val, i) => (
              { name: val[0], value: val[1].value, fill: colors[i] }
            ))}
            dataKey="value"
            cx={110}
            cy={110}
            innerRadius={60}
            outerRadius={80}
            label
          />
          <Legend layout="vertical" verticalAlign="middle" wrapperStyle={style} />
        </PieChart>
      </div>
    </div>
  </div>
);

export default PlayerStatistics;
