/* eslint no-plusplus: ["error", { "allowForLoopAfterthoughts": true }] */

// function to grab unique players, and assign them a list of their played characters
// should only be used on the Heats section of the data (data.Heats), & after it has been flattened
function orderPlayers(arr) {
  const playersChars = {};
  for (let i = 0; i < arr.length; i++) {
    if (playersChars[arr[i].name]) {
      playersChars[arr[i].name].push(arr[i].character);
    } else {
      playersChars[arr[i].name] = [arr[i].character];
    }
  }
  return playersChars;
}


module.exports = {
  orderPlayers,
};
