import React, { PureComponent } from 'react';
import {
  PieChart, Pie, Tooltip, Legend,
} from 'recharts';

import data from '../../data';
import { sortedWinnerData, sortedChampWinnerData } from '../../utils/players';

const _ = require('lodash/core');

// overall winners data
const flattenRaces = _.flattenDeep(_.map(data.Heats, 'races'));
const winnersData = sortedWinnerData(flattenRaces);
const topThree = winnersData.slice(0, 3);

// championship winners data
const flattenHeats = _.flattenDeep(_.map(data.Heats));
const champsData = sortedChampWinnerData(flattenHeats);
const topThreeChamps = champsData.slice(0, 3);

const colors = ['#4ce1b6', '#70bbfd', '#ff4861', '#4ce1b6', '#70bbfd', '#ff4861', '#4ce1b6', '#70bbfd', '#ff4861'];

const data02 = [{ name: 'Minor Bugs', value: 150, fill: '#4ce1b6' },
  { name: 'Resolved Bugs', value: 75, fill: '#70bbfd' },
  { name: 'Resolved Bugs', value: 25, fill: '#70bbfd' },
  { name: 'Critical Bugs', value: 75, fill: '#ff4861' }];

const style = {
  top: 30,
  left: 240,
  width: 150,
  lineHeight: '24px',
};

const Statistics = () => (

  <div>
    {/* project-summary__statistic */}
    <div>
      {/* project-summary__statistic-title */}
      <p>Top Overall Wins</p>
      <PieChart width={240} height={220}>
        <Tooltip />
        <Pie
          data={topThree.map((val, i) => (
            { name: val[0], value: val[1], fill: colors[i] }
          ))}
          dataKey="value"
          cx={110}
          cy={110}
          innerRadius={60}
          outerRadius={80}
          label
        />
        <Legend layout="vertical" verticalAlign="middle" wrapperStyle={style} />
      </PieChart>
    </div>
    <div>
      {/* project-summary__statistic-title */}
      <p>label 2</p>
      <PieChart width={240} height={250}>
        <Tooltip />
        <Pie
          data={topThreeChamps.map((val, i) => (
            { name: val[0], value: val[1], fill: colors[i] }
          ))}
          dataKey="value"
          cx={110}
          cy={110}
          innerRadius={60}
          outerRadius={80}
          label
        />
        <Legend layout="vertical" verticalAlign="middle" wrapperStyle={style} />
      </PieChart>
    </div>
  </div>
);

export default Statistics;
